import { LinearGradient } from 'expo-linear-gradient';
import { Icon } from '@rneui/themed';

export default function Bai1({navigation}) {
  return (
    <LinearGradient
      // Background Linear Gradient
      colors={['rgba(241,241,247,1)', 'rgba(255,255,255,1)', 'rgba(90,220,255,1)']}
      style={styles.container}
    >
      <SafeAreaView style={styles.container}>
        

        <Image style={styles.image} source={{uri: "https://www.svgrepo.com/show/532323/lock-alt.svg"}}/>

        <Text style={styles.textTitle}>FORGET PASSWORD</Text>  

        <Text style={styles.textDescription}>Provide your accountâ€™s email for which you want to reset your password</Text>  

        <View style={styles.containerInput} >
          <Icon style={styles.emailIcon} name="ios-search"/>
          < TextInput
            style={styles.textInput}
            onChangeText={() => {}}
            placeholder="Email"
            keyboardType="numeric"
          />
        </View> 
        <View style={styles.containerBtn}>
          <TouchableOpacity style={styles.btn}>
            <Text style={styles.btnText}>LOGIN</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.btn}>
            <Text style={styles.btnText}>SIGN UP</Text>
          </TouchableOpacity>
        </View>
      </SafeAreaView>
    </LinearGradient>

  );
}

